import "../Estilos/integradora.css";  // Importa los estilos correctamente
import { Card, Container, Form } from 'react-bootstrap';
import { useState } from 'react';

function Integradora() {

  const [cita, setCita] = useState({});
  const [isEnable, setIsEnable] = useState(true);
  
  const onChange = (e) => {
    e.preventDefault();
    const obj = { ...cita, [e.target.name]: e.target.value };
    setCita(obj);
    
    if (
      cita.respuesta1 && cita.respuesta2 &&
      cita.respuesta3 && cita.respuesta4 &&
      cita.respuesta5 && cita.respuesta6
    ) {
      setIsEnable(false);
    }
  }

  return (
    <Container>
      <Card>
        <Card.Body>
          <Card.Title>Encuesta servicios escolares</Card.Title>
          <Form>
            <Form.Group>
              <Form.Label>1. Nombre y correo</Form.Label>
              <Form.Control onChange={onChange} name="respuesta1" as="textarea" placeholder="Ingresa la respuesta1" />
            </Form.Group>
            <Form.Group>
              <Form.Label>2. Función que ejerces en la institución</Form.Label>
              <Form.Control onChange={onChange} name="respuesta2" as="textarea" placeholder="Ingresa la respuesta2" />
            </Form.Group>
            <Form.Group>
              <Form.Label>3. ¿Qué opinas del maestro1?</Form.Label>
              <Form.Control onChange={onChange} name="respuesta3" as="textarea" placeholder="Ingresa la respuesta3" />
            </Form.Group>
            <Form.Group>
              <Form.Label>4. ¿Qué opinas del maestro2?</Form.Label>
              <Form.Control onChange={onChange} name="respuesta4" as="textarea" placeholder="Ingresa la respuesta4" />
            </Form.Group>
            <Form.Group>
              <Form.Label>5. ¿Qué opinas de las instalaciones de la universidad?</Form.Label>
              <Form.Control onChange={onChange} name="respuesta5" as="textarea" placeholder="Ingresa la respuesta5" />
            </Form.Group>
            <Form.Group>
              <Form.Label>6. ¿Cómo te ha tratado el personal docente, cajas, seguridad y de servicios?</Form.Label>
              <Form.Control onChange={onChange} name="respuesta6" as="textarea" placeholder="Ingresa la respuesta6" />
            </Form.Group>
            <button disabled={isEnable}>Enviar</button>
          </Form>
        </Card.Body>
      </Card>
    </Container>

  );
}

export default Integradora;
